sys_variable_value.value:
(function execute(inputs, outputs) {

  var regions = [];
  if (inputs.status == '200') {
    var payload = JSON.parse(inputs.responseBody)
    for(var i=0; i<payload.data.length; i++){
      var linodeRegion = payload.data[i];
      var region = {'label': linodeRegion.country.toUpperCase() + " - " + linodeRegion.id  ,
                  'name':linodeRegion.id}; 
      regions.push(region);  
    }
  }
  regions.sort(function(a, b){ return a.label < b.label ? -1: 1 });  
  outputs.region = { data: regions };               

})(inputs, outputs);

