sys_variable_value.value:
(function execute(inputs, outputs) {
  var images = [];
  if (inputs.status == '200') {
    var payload = JSON.parse(inputs.responseBody);
    for(var i=0; i<payload.data.length; i++){
      var linodeImage = payload.data[i];

      if (linodeImage.deprecated == false) {
        var label_prefix = "";
        if (!linodeImage.is_public) {
          label_prefix = "-Private: ";
        }
        var image = {'label': label_prefix + linodeImage.label ,
                       'name':linodeImage.id}; 
        images.push(image);
      }

    }
  }

  images.sort(function(a, b){ return a.label.toUpperCase() < b.label.toUpperCase() ? -1: 1 });
  outputs.image = { data: images }; // final properly formatted output                 

})(inputs, outputs);

